mapper.select:
SELECT totalVisits, totalUser, collectionVolume, create_time, CONCAT(IFNULL(ROUND(collectionVolume / totalUser * 100, 2),0), '%') as collectionRate, IFNULL(CONCAT( FLOOR(totalTime / totalUser / 60), ' 分钟 ', totalTime / totalUser % 60, ' 秒' ),0) as averageTime FROM (SELECT IFNULL(SUM(wfsc.total_visits), 0) as totalVisits, count(wfsc.id) as totalUser, wfsc.create_time, IFNULL((SELECT count(wfsa.id) from we_form_survey_answer wfsa WHERE wfsa.belong_id=wfsc.belong_id
AND date_format(wfsa.create_time, '%Y-%m-%d' ) BETWEEN #{weFormSurveyCount.beginTime} and #{weFormSurveyCount.endTime}
), 0) as collectionVolume, IFNULL(SUM(wfsc.total_time),0) as totalTime FROM we_form_survey_count wfsc LEFT JOIN we_form_survey_catalogue wfsce ON wfsc.belong_id=wfsce.id WHERE wfsc.belong_id = #{weFormSurveyCount.belongId}
and date_format(wfsc.create_time, '%Y-%m-%d' ) BETWEEN #{weFormSurveyCount.beginTime} and #{weFormSurveyCount.endTime}
AND wfsce.channels_name=#{weFormSurveyCount.channelsName}
) as subquery GROUP BY create_time

SELECT date_format( sdd.date, '%Y-%m-%d' ) AS date, IFNULL(SUM(wfsc.total_visits), 0) as totalVisits, count(wfsc.id) as totalUser, IFNULL((SELECT count(wfsa.id) from we_form_survey_answer wfsa WHERE wfsa.belong_id=wfsc.belong_id
AND date_format(wfsa.create_time, '%Y-%m-%d' ) BETWEEN #{weFormSurveyCount.beginTime} and #{weFormSurveyCount.endTime}
), 0) as collectionVolume FROM sys_dim_date sdd LEFT JOIN we_form_survey_count wfsc ON date_format( sdd.date, '%Y-%m-%d' ) =date_format( wfsc.create_time, '%Y-%m-%d' ) LEFT JOIN we_form_survey_catalogue wfsce ON wfsc.belong_id=wfsce.id WHERE wfsc.belong_id = #{weFormSurveyCount.belongId}
AND date_format( sdd.date, '%Y-%m-%d' ) BETWEEN '2023-02-19' and '2023-07-21'
AND wfsce.channels_name=#{weFormSurveyCount.channelsName}
GROUP BY date_format( sdd.date, '%Y-%m-%d' )
