mapper.select:
select id, kf_id, open_kf_id, user_id, (select su.user_name from sys_user su where su.we_user_id = wks.user_id and su.del_flag=0 limit 1) as user_name, (select group_concat(wd.dept_name) from sys_user wu,sys_dept wd where find_in_set(wd.dept_id,wu.dept_id) and wu.we_user_id = wks.user_id and wd.del_flag = 0) as dept_name, status from we_kf_servicer wks
and wks.kf_id = #{kfId}
and wks.open_kf_id = #{openKfId}
and wks.status = #{status}
and wks.user_id in
#{userId}
and DATE_FORMAT(wks.create_time,'%Y-%m-%d') >= DATE_FORMAT(#{beginTime},'%Y-%m-%d')
and DATE_FORMAT(wks.create_time,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d')
and wks.del_flag = 0
