mapper.select:
SELECT content, content_type, wm.real_type, wm.material_id, IFNULL((SELECT GROUP_CONCAT(DISTINCT wu.user_name) as userName FROM sys_user wu WHERE wu.we_user_id=wm.creator),creator) as creator, (SELECT GROUP_CONCAT(wu.user_name) FROM sys_user wu WHERE FIND_IN_SET(wu.we_user_id,wm.no_add_user)) as noAddUserName, (SELECT GROUP_CONCAT(wu.we_user_id) FROM sys_user wu WHERE FIND_IN_SET(wu.we_user_id,wm.no_add_user)) as noAddUser, (SELECT GROUP_CONCAT(wu.user_name) FROM sys_user wu WHERE FIND_IN_SET(wu.we_user_id,wm.add_user)) as addUserName, (SELECT GROUP_CONCAT(wu.we_user_id) FROM sys_user wu WHERE FIND_IN_SET(wu.we_user_id,wm.add_user)) as addUser, push_time as createTime, moment_id, (SELECT count(*) FROM we_moments_interacte wmi WHERE wmi.moment_id=wm.moment_id and wmi.interacte_type=0) as commentNum, (SELECT count(*) FROM we_moments_interacte wmi WHERE wmi.moment_id=wm.moment_id and wmi.interacte_type=1) as pointNum FROM we_moments wm
AND wm.type=#{type}
AND wm.creator=#{creator}
AND date_format(wm.push_time ,'%Y-%m-%d') BETWEEN #{beginTime} AND #{endTime}
order by wm.push_time desc
